#include<iostream>
using namespace std;

int recursiveSum( int n )
{
    int sum = 0;
    while( n > 0 )
    {
        sum += n % 10;
        n /= 10;
    }
    return sum <= 9 ? sum : recursiveSum(sum);
}

int main()
{
    int t;
    cin >> t;
    while(t--)
    {
        int n;
        cin >> n;
        cout << recursiveSum(n) << endl;
    }

    return 0;
}

