#include<iostream>
#include<cmath>
using namespace std;

bool WordSquare(std::string letters) {
    bool retVal  = false;
    int strLength = letters.length();
    // std::cout << strLength << std::endl;
    if (sqrt(strLength)!=floor(sqrt(strLength))) {
        retVal = false;
        return retVal;
    }    
    int squareLength = sqrt(strLength);
    //test if count of all are even
    //create a hash
    unsigned int hash[26] = {0}; //all initial count is 0
    //std::cout << (int)'Z' << std::endl;
    for(char& c : letters) {
        unsigned int hashIndex = (int)c - (int)'A';
        hash[hashIndex]++;
    }
    //calculate if any is odd
    bool isOdd = false;
    int countSpareOdd = 0;
    for(unsigned int i=0; i<26; i++){
        if(hash[i]%2!=0){
            countSpareOdd++;
            isOdd = true;
        }
    }
    if(isOdd ==true && countSpareOdd <= squareLength)
        retVal = true;
    else if(isOdd == false)
        retVal = true;
    else
        retVal = false;
    return retVal;
}


int main()
{
    int t;
    cin>>t;
    while(t--)
    {
        string s;
        cin>>s;
        cout<< ( WordSquare(s) ? "true\n" : "false\n" );
    }
    return 0;
}

#include<iostream>
using namespace std;

char p[255];
char pt[255];
bool used[255];
std::string substitutionCipherDecryption(std::string x, std::string s, std::string e) {
    for(int i = 0; i < s.size(); ++i) {
        p[s[i]] = e[i];
        used[e[i]] = true;
    }
    int j = 'a';
    for(char i = 'a'; i <= 'z'; ++i) {
        if(p[i]) continue;
        while(used[j]) ++j;
        p[i] = j;
        used[j] = true;
    }
    for(int i = 0; i < 255; ++i) {
        pt[p[i]] = i;
    }
    for(int i = 0; i < x.size(); ++i) {
        x[i] = pt[x[i]];
    }
    return x;
}



int main()
{
     int t;
    cin >> t;
    while(t--)
    {
        string c, x, y;
        cin >> c >> x >> y;
        cout << substitutionCipherDecryption(c,x,y) << endl;
    }
    return 0;
}

