void fun(int a, int b,int c)
{
    if((b+c)<=a)
    {
        printf("both\n");
    }
    else if((b<=a)&&(c>a))
    {
        printf("first\n");
    }

    else if((b>a)&&(c<=a))
    {
        printf("second\n");
    }
    else if((b<=a)&&(c<=a)&&((b+c)>a))
    {
        printf("either\n");
    }
    else if((b>a)&&(c>a))
    {
        printf("none\n");
    }

}

